import React from 'react';
import '../App.css';
import './WelcomePage.css';

const WelcomePage = () => {
    // get both pupils
    const pupils = document.querySelectorAll(".eye .pupil");
    window.addEventListener("mousemove", (e) => {
    pupils.forEach((pupil) => {
        // get x and y postion of cursor
        var rect = pupil.getBoundingClientRect();
        var x = (e.pageX - rect.left) / 30 + "px";
        var y = (e.pageY - rect.top) / 30 + "px";
        pupil.style.transform = "translate3d(" + x + "," + y + ", 0px)";
    });
    });
  return (
    <div id="welcome-section" class="welcome-section">
      <div className="cloud">
        {/* <div class="eyes"> */}
            <div class="eye">
                <div class="pupil"></div>
            </div>
            <div class="eye">
                <div class="pupil"></div>
            </div>
        {/* </div> */}
      </div>
      <h1><span id="single-char">J</span>avier <span id="single-char">S</span>antana,
        <div id="hidden"></div>
        <span id="welcome-animation">Cloud Engineer</span>
      </h1>
      <p>I produce quality, accesscible, and elegant programmatic solutions.</p>
    </div>
  );
};

export default WelcomePage;
